<template>
	<div @click="" id="downloadLiecaiWrapper" class="download-liecai-wrapper">
		<img id="downloadLiecai" class="download-liecai" src="data:image/png;base64,">
	</div>
</template>
<script>
	import {addClass ,removeClass , scrollTop } from './../../common/dom'
	import comm from './../../common/common'
	export default {
		created(){
			this.throttleLoad=comm.throttle(()=>{
				this.scrollEvent()
			},50)
			window.addEventListener('scroll',this.throttleLoad)
		},
		methods:{
			scrollEvent:function(){
				var downloadLiecaiWrapper = document.getElementById("downloadLiecaiWrapper")
			    if(scrollTop() > 0){
			    	addClass(downloadLiecaiWrapper,'translate-status')
			    }else{
			    	removeClass(downloadLiecaiWrapper,'translate-status')
			    }
			}
		},
		destroyed(){
			window.removeEventListener('scroll',this.throttleLoad)
		}
	}
</script>
<style scoped>
	#download-liecai {
	    position: fixed;
	    bottom: 0;
	    width: 100%;
	    text-align: center;
	    z-index: 999;
	}
	.download-liecai-wrapper{
	    position: fixed;
	    bottom: 0;
	    left: 0;
	    width: 100%;
	    height: 1rem;
	    z-index: 999;
	    background-color: rgba(0,0,0,.6);
	    transition:all .5s;

	}
	.translate-status{
	    transform:translate3d(0,1rem,0)
	}
</style>
